mapper.select:
select isnull(start_Voltage,0) start_Voltage
from C_mach
WHERE mach_no =  #{mach_no}

select  maintain_time as currentServiceTime   
From C_mach
WHERE mach_no = #{mach_no}

select  stop_time as currentServiceTime   
From C_mach
WHERE mach_no = #{mach_no}

select mach_no, dbo.mach_name(mach_no) stationNumber, creat_time, end_num-start_num, dbo.sum_numlank1(mach_no) sumaddOli
from Ap_lank 
WHERE mach_no = #{st_no}

select mach_no, mach_type, st_state, city_electricity, al_voltage, isnull(dbo.sum_time(mach_no),0)/60 sum_time ,dbo.sumnum_time(mach_no) sumnum_time,state,isnull(start_Vo,0) start_Vo, state1,isnull(Expr2,0)-isnull(dbo.sum_time(mach_no),0)/60 maintain_time,isnull(lank_level,0) lank_per,st_current,isnull(cabinet_temperature,0) external_temperature,isnull(lank_level,0)*isnull(volumeno,0)/100 lank_level from View_mach1 where 1=1
AND cus_no = #{user_cus}
and DATEDIFF(mi,inter_time,GETDATE()) <= 3

select mach_no, mach_type, st_state, city_electricity, al_voltage, isnull(dbo.sum_time(mach_no),0)/60 sum_time,dbo.sumnum_time(mach_no) sumnum_time,state,isnull(start_Vo,0) start_Vo, state1,isnull(Expr2,0)-isnull(dbo.sum_time(mach_no),0)/60 maintain_time,isnull(lank_level,0) lank_per,st_current,isnull(cabinet_temperature,0) external_temperature,isnull(lank_level,0)*isnull(volumeno,0)/100 lank_level from View_mach1 where 1=1
AND cus_no = #{user_cus}
and (DATEDIFF(mi,inter_time,GETDATE())>3 or inter_time is null)

select mach_no, mach_type, st_state, city_electricity, al_voltage, isnull(dbo.sum_time(mach_no),0)/60 sum_time ,dbo.sumnum_time(mach_no) sumnum_time,state,isnull(start_Vo,0) start_Vo, state1,isnull(Expr2,0)-isnull(dbo.sum_time(mach_no),0)/60 maintain_time,isnull(lank_level,0) lank_per,st_current,isnull(cabinet_temperature,0) external_temperature,isnull(lank_level,0)*isnull(volumeno,0)/100 lank_level from View_mach1 where 1=1
AND cus_no = #{user_cus}
and isnull(Expr1,1)=0

select mach_no, mach_type, st_state, city_electricity, al_voltage, isnull(dbo.sum_time(mach_no),0)/60 sum_time ,dbo.sumnum_time(mach_no) sumnum_time,state,isnull(start_Vo,0) start_Vo, state1,isnull(Expr2,0)-isnull(dbo.sum_time(mach_no),0)/60 maintain_time,isnull(lank_level,0) lank_per,st_current,isnull(cabinet_temperature,0) external_temperature,isnull(lank_level,0)*isnull(volumeno,0)/100 lank_level from View_mach1 where 1=1
AND cus_no = #{user_cus}
and isnull(Expr1,1) =1



select mach_no, mach_type, st_state, city_electricity, al_voltage, isnull(dbo.sum_time(mach_no),0)/60 sum_time
,dbo.sumnum_time(mach_no) sumnum_time,state,isnull(start_Vo,0) start_Vo,
state1,isnull(Expr2,0)-isnull(dbo.sum_time(mach_no),0)/60 maintain_time,isnull(lank_level,0)
lank_per,st_current,isnull(cabinet_temperature,0)
external_temperature,isnull(lank_level,0)*isnull(volumeno,0)/100 lank_level
from View_mach1
WHERE 1 = 1

    AND cus_no = #{user_cus}


    AND state = #{state}


    AND st_state = #{st_state}


    AND mach_type = #{mach_type}


    AND fuel_type = #{fuel_type}


    AND mach_no like '%'+#{search1}+'%'


    AND Acity_electricity = #{Acity_electricity}

select mach_no,mach_name, st_state,st_coordinate
 from View_mach1
where mach_no = #{mach_no}
